                    city = strcity.substring(0, indexofcity);
                    strcity = strcity.substring(indexofcity + 1);
                    document.getElementById("ddlCity").add(new Option(city, city));
                }
                else {
                    document.getElementById("ddlCity").add(new Option(strcity, strcity));
                    break;
                }
            }
        }
